var product_rating = getRandomNumber(0, 50)/10; // Curva de potencia teórica (KWh)
var product_reviews = Math.round(getRandomNumber(0, 600)); // Dirección del viento (°)
var url = "http://127.0.0.1:1880/#flow/23367ebca52d19af";
// Obtener la marca de tiempo actual en formato ISO 8601 (UTC)
var timestamp = new Date().toISOString();

// Construir el punto de datos
var contenido = 
    {
        "name": product_name,
        "price": product_price,
        "rating": product_rating,
        "reviews": product_reviews,
        "url": url,
        "timestamp": timestamp
    }


